function Auth(){this.maskwrapper=$(".mask-wrapper")}Auth.prototype.run=function(){var n=this;n.listenShowEvent(),n.listenSwitchEvent(),n.listenSigninEvent()},Auth.prototype.showEvent=function(){this.maskwrapper.show()},Auth.prototype.hideEvent=function(){this.maskwrapper.hide()},Auth.prototype.listenSwitchEvent=function(){$(".switch").click(function(){var n=$(".scroll-wrapper"),t=n.css("left");(t=parseInt(t))<0?n.animate({left:"0"}):n.animate({left:"-400px"})})},Auth.prototype.listenShowEvent=function(){var n=this,t=$(".signin-btn"),e=$(".signup-btn"),i=$(".close-btn"),o=$(".scroll-wrapper");t.click(function(){n.showEvent(),o.css({left:"0"})}),e.click(function(){n.showEvent(),o.css({left:"-400"})}),i.click(function(){n.hideEvent()})},Auth.prototype.listenSigninEvent=function(){var o=this,n=$(".signin-group"),i=n.find("input[name='telephone']"),s=n.find("input[name='password']"),r=n.find("input[name='remember']");n.find(".submit-btn").click(function(){var n=i.val(),t=s.val(),e=r.prop("checked");xfzajax.post({url:"/account/login/",data:{telephone:n,password:t,remember:e?1:0},success:function(n){if(200===n.code)o.hideEvent(),window.location.reload();else{var t=n.message;if("string"==typeof t||t.constructor==String)window.messageBox.show(t);else for(var e in t){var i=t[e][0];window.messageBox.show(i)}}},fail:function(n){console.log(n)}})})},$(function(){(new Auth).run()});